mapper.select:
SELECT BNO, TITLE, CONTENT, WRITER, REGDATE, UPDATEDATE, LIKECNT, REPLYCNT
FROM TBL_FREE_BOARD
WHERE BNO = #{bno}

mapper.update:
UPDATE TBL_FREE_BOARD
SET TITLE = #{title}, CONTENT = #{content}, UPDATEDATE = SYSDATE
WHERE BNO = #{bno}

UPDATE TBL_FREE_BOARD
SET REPLYCNT = REPLYCNT + #{amount}
WHERE BNO = #{bno}
