mapper.select:
SELECT BNO, TITLE, CONTENT, WRITER, TO_CHAR(REGDATE, 'YYYY-MM-DD') as REGDATE, TO_CHAR(UPDATEDATE, 'YYYY-MM-DD') as UPDATEDATE, LIKECNT, READCNT, REPLYCNT
FROM TBL_REVIEW_BOARD
WHERE BNO = #{bno}

mapper.update:
UPDATE TBL_REVIEW_BOARD
SET TITLE = #{title}, CONTENT = #{content}, UPDATEDATE = SYSDATE
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD
SET READCNT = READCNT + 1
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD
SET REPLYCNT = REPLYCNT + #{amount}
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD
SET LIKECNT = LIKECNT + #{amount}
WHERE BNO = #{bno}

mapper.select:
SELECT COUNT(BNO) 
FROM TBL_REVIEW_LIKE 
WHERE BNO = #{bno} AND USERID = #{userId}

mapper.update:
UPDATE TBL_REVIEW_BOARD 
SET LIKECNT = LIKECNT + 1 
WHERE BNO = #{bno}

UPDATE TBL_REVIEW_BOARD 
SET LIKECNT = LIKECNT - 1 
WHERE BNO = #{bno}
